SELECT MERCHANT_GROUPS.GROUP_NAME, ORDERS.ORDER_ID, ORDERS.MER_USER_ID, ORDERS.MEMBER_ID, ORDERS.MER_ID, ORDERS.SP_CODE, ORDERS.MER_GROUP_ID, ORDERS.ORDER_CODE, ORDERS.CREATE_DATE, ORDERS.TOTAL_PRICE, ORDERS.FINAL_PRICE, ORDERS.MEMBER_DISCOUNT, ORDERS.MEMBER_CODE, ORDERS.PAYMENT_INFO, ORDERS.ORDER_STATUS, ORDERS.PAY_AMOUNT, ORDERS.PAY_DATE, ORDERS.PAY_METHOD, ORDERS.PAY_BILL_NUMBER, ORDERS.CASH_AMOUNT, ORDERS.CASH_CHANGE, ORDERS.MEMBER_BALANCE_PAY, ORDERS.DISCOUNT_AMOUNT, ORDERS.CARD_COUPON_PAY, ORDERS.OPERATOR_ID, ORDERS.OPERATOR_NAME, ORDERS.ORDER_PROFIT, ORDERS.ORDER_CONFIRM, ORDERS.ORDER_CONFIRM_DATE, ORDERS.ORDER_CONFIRM_PERSON, ORDERS.TERM_CODE, ORDERS.CHARGE_RATE, (ORDERS.CHARGE_AMOUNT*0.01) AS CHARGE_AMOUNT, ORDERS.TERM_SN FROM ORDERS JOIN MERCHANT_GROUPS ON ORDERS.MER_GROUP_ID = MERCHANT_GROUPS.MER_GROUP_ID where ORDERS.MER_ID = #{merId,jdbcType=VARCHAR}
AND DATE_FORMAT(ORDERS.CREATE_DATE,'%Y-%m-%d') >= #{createDateText,jdbcType=VARCHAR}
AND DATE_FORMAT(ORDERS.CREATE_DATE,'%Y-%m-%d') <= #{endDateText,jdbcType=VARCHAR}
AND ORDERS.MER_GROUP_ID = #{merGroupId,jdbcType=VARCHAR}
AND ORDERS.ORDER_CODE = #{orderCode,jdbcType=VARCHAR}
AND TERM_CODE = #{termCode,jdbcType=VARCHAR}
AND TERM_SN = #{termSn,jdbcType=VARCHAR}
order by ORDERS.CREATE_DATE desc limit #{offset},#{limit}

 SELECT * FROM ZHIGOU	
